/**
 * The main lazyload class.
 *
 * This class defines all code necessary to run during the plugin's activation.
 *
 * @since      1.0.0
 * @package    WP_Godspeed
 * @subpackage WP_Godspeed/includes
 * @author     WP Godspeed <hello@wpgodspeed.io>
 */
if ( ! class_exists( 'WP_Godspeed_Lazyload_Plugin' ) )
{
	class WP_Godspeed_Lazyload_Plugin extends WP_Godspeed_Admin {

		function addaction( $h )
		{
			$p    = 10;
			$args = 999;
			$m    = $this->sanitize_method( $h );
			$b    = func_get_args();
			unset( $b[0] );
			foreach ( (array) $b as $a )
			{
				if ( is_int( $a ) )
				{
					$p = $a;
				}
				else
				{
					$m = $a;
				}
			}
			return add_action( $h, array( $this, $m ), $p, $args );
		}

		function addfilter( $f )
		{
			$p    = 10;
			$args = 2;
			$m    = $this->sanitize_method( $f );
			$b    = func_get_args();
			unset( $b[0] );
			foreach ( (array) $b as $a )
			{
				if ( is_int( $a ) )
				{
					$p = $a;
				}
				else
				{
					$m = $a;
				}
			}
			return add_filter( $f, array( $this, $m ), $p, $args );
		}

		private function sanitize_method( $m )
		{
			return str_replace( array( '.', '-' ), array( '_DOT_', '_DASH_' ), $m );
		}
	}
}

class WP_Godspeed_Lazyload extends WP_Godspeed_Lazyload_Plugin {

	public $lazyload_init;
	public $threshold;
	public $images;
	public $iframes;
	public $youtube;
	public $debug;
	public $pages_enabled;
	public $pages_disabled;
	public $status;

	public function __construct()
	{
		$this->addaction( 'plugins_loaded' );
	}

	public function plugins_loaded()
	{
		$this->images         = get_option( $this->option_name . '_lazyload_images' );
		$this->iframes        = get_option( $this->option_name . '_lazyload_iframes' );
		$this->youtube        = get_option( $this->option_name . '_lazyload_youtube' );
		$this->debug          = get_option( $this->option_name . '_lazyload_debug' );
		$this->debug          = FALSE;
		$this->threshold      = 1500; //500
		$this->pages_enabled  = ''; //[ 1212 ];
		$this->pages_disabled = ''; //[ 1308 ];

		if ( $this->images || $this->iframes || $this->youtube )
		{
			$this->addaction( 'init' );
		}
	}

	public function init()
	{
		$this->addaction( 'wp', 'wpgods_lazyload_disable_on_amp', PHP_INT_MAX );
		$this->addaction( 'wp', 'wpgods_lazyload_special_pages', PHP_INT_MAX );

		if ( ( ! $this->images && ! $this->iframes ) || ! apply_filters( 'wpgods_do_lazyload', TRUE ) )
		{
			return;
		}

		if ( ! is_admin() )
		{
			$this->addaction( 'template_redirect' );
			$this->addaction( 'wp_footer',          'wpgods_lazyload_script',  PHP_INT_MAX );
			$this->addaction( 'wp_enqueue_scripts', 'wpgods_lazyload_enqueue', PHP_INT_MAX );

			if ( $this->images )
			{
				$this->addfilter( 'get_avatar',           'wpgods_lazyload_images',    PHP_INT_MAX );
				$this->addfilter( 'the_content',          'wpgods_lazyload_images',    PHP_INT_MAX );
				$this->addfilter( 'widget_text',          'wpgods_lazyload_images',    PHP_INT_MAX );
				$this->addfilter( 'get_image_tag',        'wpgods_lazyload_images',    PHP_INT_MAX );
				$this->addfilter( 'post_thumbnail_html',  'wpgods_lazyload_images',    PHP_INT_MAX );
				$this->addfilter( 'wpgods_lazyload_html', 'wpgods_lazyload_on_srcset', PHP_INT_MAX );
			}
			if ( $this->iframes )
			{
				$this->addfilter( 'the_content', 'wpgods_lazyload_iframes', PHP_INT_MAX );
				$this->addfilter( 'widget_text', 'wpgods_lazyload_iframes', PHP_INT_MAX );
			}
			if ( $this->debug )
			{
				$this->addaction( 'wpgods_lazyload_content', 'debug' );
			}
		}
	}

	public function wpgods_lazyload_script()
	{
		if ( ( ! $this->images && ! $this->iframes ) || ! apply_filters( 'wpgods_do_lazyload', TRUE ) )
		{
			return;
		}

		//filters the threshold at which lazyload is triggered
		$threshold = apply_filters( 'wpgods_lazyload_threshold', $this->threshold );

		echo '<script>(function(w, d) {
		var b = d.getElementsByTagName("body")[0];
		var s = d.createElement("script"); s.async = true;
		s.src = "' . plugin_dir_url( __DIR__ ) . 'public/js/lazyload.js";
		w.lazyLoadOptions = {
			elements_selector: "img, iframe",
			data_src: "lazy-src",
			data_srcset: "lazy-srcset",
			skip_invisible: false,
			class_loading: "lazyloading",
			class_loaded: "lazyloaded",
			threshold: ' . $threshold . ',
			callback_load: function(element) {
				if ( element.tagName === "IFRAME" && element.dataset.wpgsLazyload == "fitvidscompatible" ) {
					if (element.classList.contains("lazyloaded") ) {
						if (typeof window.jQuery != "undefined") {
							if (jQuery.fn.fitVids) {
								jQuery(element).parent().fitVids();
							}
						}
					}
				}
			}
		};
		b.appendChild(s);
		}(window, document));

		//listen to the initialized event
		window.addEventListener(\'LazyLoad::Initialized\', function (e) {
			//get the instance and put it in the lazyLoadInstance variable
			var lazyLoadInstance = e.detail.instance;

			var observer = new MutationObserver(function(mutations) {
				mutations.forEach(function(mutation) {
					lazyLoadInstance.update();
				} );
			} );

			var b      = document.getElementsByTagName("body")[0];
			var config = { childList: true, subtree: true };

			observer.observe(b, config);
		}, false);
		</script>';

		if ( $this->youtube )
		{
			echo <<<EOD
			<script>function lazyLoadThumb(e){var t='<img src="https://i.ytimg.com/vi/ID/hqdefault.jpg">',a='<div class="play"></div>';return t.replace("ID",e)+a}function lazyLoadYoutubeIframe(){var e=document.createElement("iframe"),t="https://www.youtube.com/embed/ID?autoplay=1";e.setAttribute("src",t.replace("ID",this.dataset.id)),e.setAttribute("frameborder","0"),e.setAttribute("allowfullscreen","1"),this.parentNode.replaceChild(e,this)}document.addEventListener("DOMContentLoaded",function(){var e,t,a=document.getElementsByClassName("gll-youtube-player");for(t=0;t<a.length;t++)e=document.createElement("div"),e.setAttribute("data-id",a[t].dataset.id),e.innerHTML=lazyLoadThumb(a[t].dataset.id),e.onclick=lazyLoadYoutubeIframe,a[t].appendChild(e)});</script>
EOD;
		}
	}

	public function wpgods_lazyload_enqueue()
	{
		if ( ( ! $this->images && ! $this->iframes ) || ! apply_filters( 'wpgods_do_lazyload', TRUE ) )
		{
			return;
		}

		if ( $this->youtube )
		{
			//<svg height="100%" version="1.1" viewBox="0 0 68 48" width="100%"><path class="ytp-large-play-button-bg" d="M66.52,7.74c-0.78-2.93-2.49-5.41-5.42-6.19C55.79,.13,34,0,34,0S12.21,.13,6.9,1.55 C3.97,2.33,2.27,4.81,1.48,7.74C0.06,13.05,0,24,0,24s0.06,10.95,1.48,16.26c0.78,2.93,2.49,5.41,5.42,6.19 C12.21,47.87,34,48,34,48s21.79-0.13,27.1-1.55c2.93-0.78,4.64-3.26,5.42-6.19C67.94,34.95,68,24,68,24S67.94,13.05,66.52,7.74z" fill="#212121" fill-opacity="0.8"></path><path d="M 45,24 27,14 27,34" fill="#fff"></path></svg>
			$css =<<<EOD
.gll-youtube-player {
  position:relative;
  padding-bottom:56.23%;
  height:0;
  overflow:hidden;
  max-width:100%;
  background:#000;
  margin:5px;
}
.gll-youtube-player iframe {
  position:absolute;
  top:0;
  left:0;
  width:100%;
  height:100%;
  z-index:100;
  background:0 0;
}
.gll-youtube-player img {
  bottom:0;
  display:block;
  left:0;
  margin:auto;
  max-width:100%;
  width:100%;
  position:absolute;
  right:0;
  top:0;
  border:none;
  height:auto;
  cursor:pointer;
  -webkit-transition:.4s all;
  -moz-transition:.4s all;
  transition:.4s all;
}
.gll-youtube-player .play {
  height:79px;
  width:79px;
  left:50%;
  top:50%;
  margin-left:-36px;
  margin-top:-36px;
  position:absolute;
  background:url('data:image/png;base64,') no-repeat;
  background-size:75px 75px;
  cursor:pointer;
  opacity:0.7;
}
.gll-youtube-player:hover .play {
  height:79px;
  width:79px;
  left:50%;
  top:50%;
  margin-left:-36px;
  margin-top:-36px;
  position:absolute;
  background:url('data:image/png;base64,') no-repeat;
  background-size:75px 75px;
  cursor:pointer;
  opacity:1.0;
