EOD;
			wp_register_style( 'wpgods-lazyload', FALSE );
			wp_enqueue_style( 'wpgods-lazyload' );
			wp_add_inline_style( 'wpgods-lazyload', $css );
		}
	}

	public function wpgods_lazyload_special_pages()
	{
		$this->is_page = get_the_ID();

		//turn off lazyload everywhere except for enabled pages
		if ( ! empty( $this->pages_enabled ) )
		{
			if ( is_array( $this->pages_enabled ) )
			{
				if ( array_search( get_the_ID(), $this->pages_enabled ) === FALSE )
				{
					$this->status = 'disabled';
					add_filter( 'wpgods_do_lazyload', '__return_false' );
				}
				else
				{
					$this->status = 'enabled';
				}
			}
			else
			{
				if ( is_page( $this->pages_enabled ) === FALSE )
				{
					$this->status = 'disabled';
					add_filter( 'wpgods_do_lazyload', '__return_false' );
				}
			}
		}
		else
		{
			$this->status = 'enabled';
		}

		//turn off lazyload for disabled pages
		if ( ! empty( $this->pages_disabled ) )
		{
			if ( is_array( $this->pages_disabled ) )
			{
				if ( array_search( get_the_ID(), $this->pages_disabled ) !== FALSE )
				{
					$this->status = 'disabled';
					add_filter( 'wpgods_do_lazyload', '__return_false' );
				}
				else
				{
					$this->status = 'enabled';
				}
			}
			else
			{
				if ( is_page( $this->pages_disabled ) === TRUE )
				{
					$this->status = 'disabled';
					add_filter( 'wpgods_do_lazyload', '__return_false' );
				}
			}
		}
		else
		{
			$this->status = 'enabled';
		}
	}

	public function wpgods_lazyload_images( $content )
	{
		//don't lazyload if the thumbnail is in admin, a feed, rest api or a post preview
		if ( ! $this->images || is_admin() || is_feed() || is_preview() || ( defined( 'REST_REQUEST' ) && REST_REQUEST ) || empty( $content ) || wp_script_is( 'twenty-twenty', 'enqueued' ) )
		{
			return $content;
		}

		//you can stop the lazyLoad process with a hook
		if ( ! apply_filters( 'wpgods_do_lazyload', TRUE ) )
		{
			return $content;
		}
		return preg_replace_callback( '#<img([^>]*) src=("(?:[^"]+)"|\'(?:[^\']+)\'|(?:[^ >]+))([^>]*)>#', array( $this, 'wpgods_lazyload_replace_callback' ), $content );
	}

	public function wpgods_lazyload_replace_callback( $matches )
	{
		if ( function_exists( 'wr2x_picture_rewrite' ) )
		{
			if ( wr2x_get_retina( trailingslashit( ABSPATH ) .wr2x_get_pathinfo_from_image_src( trim( $matches[2], '"' ) ) ) )
			{
				return $matches[0];
			}
		}

		$excluded_attributes = apply_filters( 'wpgods_lazyload_excluded_attributes', array(
			'data-no-lazy=',
			'data-lazy-original=',
			'data-lazy-src=',
			'data-src=',
			'data-lazysrc=',
			'data-lazyload=',
			'data-bgposition=',
			'data-envira-src=',
			'fullurl=',
			'lazy-slider-img=',
			'data-srcset=',
			'class="ls-l',
			'class="ls-bg',
		) );

		$excluded_src = apply_filters( 'wpgods_lazyload_excluded_src', array(
			'/wpcf7_captcha/',
			'timthumb.php?src',
		) );

		if ( $this->wpgods_is_excluded_lazyload( $matches[1] . $matches[3], $excluded_attributes ) || $this->wpgods_is_excluded_lazyload( $matches[2], $excluded_src ) )
		{
			return $matches[0];
		}

		//filter the lazyload placeholder on src attribute
		$placeholder  = apply_filters( 'wpgods_lazyload_placeholder', 'data:image/gif;base64,R0lGODdhAQABAPAAAP///wAAACwAAAAAAQABAEACAkQBADs=' );
		$img          = sprintf( '<img%1$s src="%4$s" data-lazy-src=%2$s%3$s>', $matches[1], $matches[2], $matches[3], $placeholder );
		$img_noscript = sprintf( '<noscript><img%1$s src=%2$s%3$s></noscript>', $matches[1], $matches[2], $matches[3] );

		//filter the lazyload html output
		$img = apply_filters( 'wpgods_lazyload_html', $img, TRUE );

		return $img . $img_noscript;
	}

	public function wpgods_is_excluded_lazyload( $string, $excluded_values )
	{
		foreach ( $excluded_values as $excluded_value )
		{
			if ( strpos( $string, $excluded_value ) !== FALSE )
			{
				return TRUE;
			}
		}
		return FALSE;
	}

	public function wpgods_lazyload_on_srcset( $content )
	{
		if ( preg_match( '/srcset=("(?:[^"]+)"|\'(?:[^\']+)\'|(?:[^ >]+))/i', $content ) )
		{
			$content = str_replace( 'srcset=', 'data-lazy-srcset=', $content );
		}

		if ( preg_match( '/sizes=("(?:[^"]+)"|\'(?:[^\']+)\'|(?:[^>]+))/i', $content ) )
		{
			$content = str_replace( 'sizes=', 'data-lazy-sizes=', $content );
		}
		return $content;
	}

	public function wpgods_lazyload_iframes( $content )
	{
		//don't lazyload if process is stopped for any of these reasons
		if ( ! $this->iframes || ! apply_filters( 'do_wpgods_lazyload_iframes', TRUE ) || is_feed() || is_preview() || ( defined( 'REST_REQUEST' ) && REST_REQUEST ) || empty( $content ) )
		{
			return $content;
		}

		$matches = array();
		preg_match_all( '/<iframe(?:.*)?src=["|\'](.*)["|\'](?:.*)?><\/iframe>/iU', $content, $matches, PREG_SET_ORDER );

		if ( empty( $matches ) )
		{
			return $content;
		}

		foreach ( $matches as $iframe )
		{
			//don't touch the gravity forms ajax iframe stuff
			if ( strpos( $iframe[0], 'gform_ajax_frame' ) )
			{
				continue;
			}

			//don't lazyload an iframe if it has data-no-lazy attribute
			if ( strpos( $iframe[0], 'data-no-lazy=' ) )
			{
				continue;
			}

			if ( $this->youtube && strpos( $iframe[1], 'youtube' ) !== FALSE )
			{
				$youtube_id = $this->wpgods_lazyload_get_youtube_id_from_url( $iframe[1] );

				if ( ! $youtube_id )
				{
					continue;
				}

				//filter the lazyload html output on youtube iframes
				$youtube_lazyload = apply_filters( 'wpgods_lazyload_youtube_html', '<div class="gll-youtube-player" data-id="' . $youtube_id . '"></div>');
				$youtube_lazyload .= '<noscript>' . $iframe[0] . '</noscript>';
				$content          = str_replace( $iframe[0], $youtube_lazyload, $content );
			}
			else
			{
				//filter the lazyload placeholder on src attribute
				$placeholder     = apply_filters( 'wpgods_lazyload_placeholder', 'about:blank' );
				$iframe_noscript = '<noscript>' . $iframe[0] . '</noscript>';

				//filter the lazyload html output on iframes
				$iframe_lazyload = apply_filters( 'wpgods_lazyload_iframe_html', str_replace( $iframe[1], $placeholder . '" data-wpgods-lazyload="fitvidscompatible" data-lazy-src="' . $iframe[1], $iframe[0]) );
				$iframe_lazyload .= $iframe_noscript;
				$content         = str_replace( $iframe[0], $iframe_lazyload, $content );
			}
		}
		return $content;
	}

	function wpgods_lazyload_get_youtube_id_from_url( $url )
	{
		$pattern = '#^(?:https?://)?(?:www\.)?(?:youtu\.be/|youtube\.com(?:/embed/|/v/|/watch\?v=))([\w-]{11})#iU';
		$result  = preg_match( $pattern, $url, $matches );

		if ( $result )
		{
			return $matches[1];
		}
		return FALSE;
	}

	public function wpgods_lazyload_disable_on_amp()
	{
		if ( defined( 'AMP_QUERY_VAR' ) && function_exists( 'is_amp_endpoint' ) && is_amp_endpoint() )
		{
			add_filter( 'wpgods_do_lazyload', '__return_false' );
		}
	}

	public function debug( $content )
	{
		$content .= "<!-- start wpgodspeed lazyload debug -->\n";
		$content .= "<!-- is_page: $this->is_page -->\n";
		$content .= "<!-- status: $this->status -->\n";
		$content .= "<!-- end wpgodspeed lazyload debug -->\n";
		return $content;
	}

	public function template_redirect()
	{
		ob_start( array( $this, 'ob' ) );
	}

	public function ob( $contents )
	{
		return apply_filters( 'wpgods_lazyload_content', $contents, $this );
	}


}
